sec.code:
par(mfrow=c(2,3))
for(i in idx_comp){ dens( SI$SI_raw[[i]], xlim=c(-2.5,2.5), col=rgb(0,0,0,0.7), show.HPDI=0.95, xlab='Difference in potential intelligibility') abline( v=0, lty=2, col=rgb(0,0,0,0.3)) mtext( text=names(SI$SI_raw)[i], side=3, adj=0, cex=1.1) }
par(mfrow=c(1,1))

par_int = c('a','aHS[1]','aHS[2]','bAm','bAmHS[1]','bAmHS[2]') model_par = par_recovery(stanfit_obj=model11, p=0.95, est_par=par_int) contrs = contrast_intel(stanfit_obj=model11, p=0.95, rope=c(-0.05,0.05))
vars_int = c('mean','HPDI_lower','HPDI_upper') model_tab = rbind(model_par[,vars_int], rep(NA,3), rep(NA,3), contrs[,vars_int]) model_tab = round(model_tab, 2) model_tab$names = c('$\\alpha
,'$\\alpha_{HS[1]}
,'$\\alpha_{HS[2]}
, '$\\beta_{A}
, NA, 'Contrasts','$\\alpha_{HS[2]} - \\alpha_{HS[1]}
) model_tab$HPDI = paste0('[', model_tab$HPDI_lower, ', ', model_tab$HPDI_upper,']') model_tab[5, 1:5] = NA model_tab[6, c(1:3,5)] = NA

par_int = c('a','aHS[1]','aHS[2]','bAm','bAmHS[1]','bAmHS[2]')
model_par = par_recovery(stanfit_obj=model12,
                         p=0.95, est_par=par_int)
contrs = contrast_intel(stanfit_obj=model12, p=0.95,
                        rope=c(-0.05,0.05))

vars_int = c('mean','HPDI_lower','HPDI_upper')
model_tab = rbind(model_par[,vars_int], rep(NA,3), rep(NA,3), contrs[,vars_int])
model_tab = round(model_tab, 2)
model_tab$names = c('$\\alpha

,'$\\alpha_{HS[1]}

,'$\\alpha_{HS[2]}

,
                    '$\\beta_{A,HS[1]}

,'$\\beta_{A,HS[2]}

, NA,
                    'Contrasts','$\\alpha_{HS[2]} - \\alpha_{HS[1]}

,
                    '$\\beta_{A,HS[2]} - \\beta_{A,HS[1]}

)
model_tab$HPDI = paste0('[', model_tab$HPDI_lower, ', ', model_tab$HPDI_upper,']')
model_tab[6, 1:5] = NA
model_tab[7, c(1:3,5)] = NA

